import React from 'react';
import { Download, ExternalLink, AlertCircle } from 'lucide-react';

function Dashboard() {
  const points = Array.from({ length: 20 }, (_, i) => `P${i + 1}`);
  const communicativeData = Array.from({ length: 20 }, (_, i) => 
    Math.min(85, Math.max(30, 30 + i * 2.5 + Math.random() * 10))
  );
  const socialData = Array.from({ length: 20 }, (_, i) => 
    Math.min(75, Math.max(20, 20 + i * 2 + Math.random() * 8))
  );

  const scoreData = [0, 1, 2, 3, 3, 4, 5, 6, 6, 7, 7];
  const cumulativeData = scoreData.map((val, i) => 
    scoreData.slice(0, i + 1).reduce((acc, n) => acc + n, 0)
  );

  const total = cumulativeData[cumulativeData.length - 1];

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-blue-100 p-8">
      <div className="max-w-7xl mx-auto space-y-8">

        {/* ... Header y Alerta ... */}

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">

          {/* Score Card Actualizado */}
          <div className="bg-white rounded-2xl p-8 shadow-xl">
            <div className="flex items-center justify-between mb-8">
              <h2 className="text-2xl font-semibold text-gray-800">Puntaje Q-CHAT 10</h2>
              <div className="bg-gradient-to-r from-blue-500 to-blue-600 text-white px-6 py-2 rounded-xl font-bold text-lg shadow-md">
                {total}/70
              </div>
            </div>
            <div className="space-y-4">
              <div className="relative h-64">
                {[...Array(8)].map((_, i) => (
                  <div
                    key={i}
                    className="absolute w-full h-px bg-gray-100"
                    style={{ bottom: `${(i * 100) / 7}%` }}
                  >
                    <span className="absolute -left-6 text-xs text-gray-400">{i * 10}</span>
                  </div>
                ))}
                <div className="absolute bottom-0 left-0 right-0 h-full">
                  <svg className="w-full h-full">
                    <path
                      d={`M ${cumulativeData.map((value, i) =>
                        `${(i * (100 / (cumulativeData.length - 1)))}% ${100 - (value * 100 / 70)}%`
                      ).join(' L ')}`}
                      fill="none"
                      stroke="#3B82F6"
                      strokeWidth="2"
                      vectorEffect="non-scaling-stroke"
                    />
                    {cumulativeData.map((value, i) => (
                      <circle
                        key={i}
                        cx={`${(i * (100 / (cumulativeData.length - 1)))}%`}
                        cy={`${100 - (value * 100 / 70)}%`}
                        r="4"
                        fill="#3B82F6"
                        className="hover:r-6 transition-all duration-200"
                      />
                    ))}
                  </svg>
                </div>
              </div>
              <div className="flex justify-between text-sm text-gray-500 pt-4 border-t border-gray-100">
                {cumulativeData.map((_, i) => (
                  <span key={i}>A{i + 1}</span>
                ))}
              </div>
            </div>
          </div>

          {/* ... TEA Risk Card y Skills Progress Card ... */}

        </div>

        {/* ... Evoluci√≥n Temporal de Habilidades y Footer ... */}

      </div>
    </div>
  );
}

export default Dashboard;
