import React, { useState } from 'react';
import { ChevronRight, ChevronLeft } from 'lucide-react';

const questions = [
  {
    id: 1,
    text: "¿Tu hijo te mira cuando lo llamas por su nombre?",
    options: ['Siempre', 'Usualmente', 'A veces', 'Raramente', 'Nunca']
  },
  {
    id: 2,
    text: "¿Qué tan fácil es para ti lograr contacto visual con tu hijo?",
    options: ['Muy fácil', 'Bastante fácil', 'Bastante difícil', 'Muy difícil', 'Imposible']
  },
  {
    id: 3,
    text: "¿Tu hijo señala para indicar que quiere algo?",
    options: ['Muchas veces al día', 'Unas cuantas veces al día', 'Unas cuantas veces por semana', 'Menos de una vez por semana', 'Nunca']
  },
  {
    id: 4,
    text: "¿Tu hijo señala para compartir interés contigo?",
    options: ['Muchas veces al día', 'Unas cuantas veces al día', 'Unas cuantas veces por semana', 'Menos de una vez por semana', 'Nunca']
  },
  {
    id: 5,
    text: "¿Tu hijo finge?",
    options: ['Muchas veces al día', 'Unas cuantas veces al día', 'Unas cuantas veces por semana', 'Menos de una vez por semana', 'Nunca']
  },
  {
    id: 6,
    text: "¿Tu hijo sigue con la mirada hacia donde tú estás mirando?",
    options: ['Muchas veces al día', 'Unas cuantas veces al día', 'Unas cuantas veces por semana', 'Menos de una vez por semana', 'Nunca']
  },
  {
    id: 7,
    text: "¿Tu hijo muestra señales de querer consolar?",
    options: ['Siempre', 'Usualmente', 'A veces', 'Raramente', 'Nunca']
  },
  {
    id: 8,
    text: "¿Cómo describirías las primeras palabras de tu hijo?",
    options: ['Muy típicas', 'Bastante típicas', 'Ligeramente inusuales', 'Muy inusuales', 'Mi hijo no habla']
  },
  {
    id: 9,
    text: "¿Tu hijo usa gestos simples?",
    options: ['Muchas veces al día', 'Unas cuantas veces al día', 'Unas cuantas veces por semana', 'Menos de una vez por semana', 'Nunca']
  },
  {
    id: 10,
    text: "¿Tu hijo se queda mirando fijamente a la nada sin un propósito aparente?",
    options: ['Muchas veces al día', 'Unas cuantas veces al día', 'Unas cuantas veces por semana', 'Menos de una vez por semana', 'Nunca']
  }
];

export default function Forms() {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [answers, setAnswers] = useState({});
  const [scoreData, setScoreData] = useState([]);

  const handleOptionSelect = (option) => {
    setAnswers(prev => ({
      ...prev,
      [questions[currentQuestion].id]: option
    }));

    // Calcular y actualizar el puntaje
    const newScoreData = [...scoreData];
    newScoreData[currentQuestion] = calculateScore();
    setScoreData(newScoreData);
  };

  const handleNext = () => {
    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(prev => prev + 1);
    }
  };

  const handlePrevious = () => {
    if (currentQuestion > 0) {
      setCurrentQuestion(prev => prev - 1);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const totalScore = scoreData.reduce((acc, score) => acc + score, 0);
    const message = totalScore > 3
      ? "Este resultado sugiere que podría ser útil una evaluación multidisciplinaria."
      : "Este resultado no indica necesariamente la necesidad de evaluación, pero puede consultar a un especialista si tiene dudas.";

    alert(`¡Cuestionario completado!\n\nPuntaje total: ${totalScore}/10\n${message}`);
  };

  const calculateScore = () => {
    const answer = answers[questions[currentQuestion].id];
  
    // Mapeo de opciones para cada pregunta
    const scoreMapping = {
      1: { // Pregunta 1: ¿Tu hijo te mira cuando lo llamas por su nombre?
        'Siempre': 0,
        'Usualmente': 0,
        'A veces': 1,
        'Raramente': 1,
        'Nunca': 1
      },
      2: { // Pregunta 2: ¿Qué tan fácil es para ti lograr contacto visual con tu hijo?
        'Muy fácil': 0,
        'Bastante fácil': 0,
        'Bastante difícil': 1,
        'Muy difícil': 1,
        'Imposible': 1
      },
      3: { // Pregunta 3: ¿Tu hijo señala para indicar que quiere algo?
        'Muchas veces al día': 0,
        'Unas cuantas veces al día': 0,
        'Unas cuantas veces por semana': 1,
        'Menos de una vez por semana': 1,
        'Nunca': 1
      },
      4: { // Pregunta 4: ¿Tu hijo señala para compartir interés contigo?
        'Muchas veces al día': 0,
        'Unas cuantas veces al día': 0,
        'Unas cuantas veces por semana': 1,
        'Menos de una vez por semana': 1,
        'Nunca': 1
      },
      5: { // Pregunta 5: ¿Tu hijo finge?
        'Muchas veces al día': 0,
        'Unas cuantas veces al día': 0,
        'Unas cuantas veces por semana': 1,
        'Menos de una vez por semana': 1,
        'Nunca': 1
      },
      6: { // Pregunta 6: ¿Tu hijo sigue con la mirada hacia donde tú estás mirando?
        'Muchas veces al día': 0,
        'Unas cuantas veces al día': 0,
        'Unas cuantas veces por semana': 1,
        'Menos de una vez por semana': 1,
        'Nunca': 1
      },
      7: { // Pregunta 7: ¿Tu hijo muestra señales de querer consolar?
        'Siempre': 0,
        'Usualmente': 0,
        'A veces': 1,
        'Raramente': 1,
        'Nunca': 1
      },
      8: { // Pregunta 8: ¿Cómo describirías las primeras palabras de tu hijo?
        'Muy típicas': 0,
        'Bastante típicas': 0,
        'Ligeramente inusuales': 1,
        'Muy inusuales': 1,
        'Mi hijo no habla': 1
      },
      9: { // Pregunta 9: ¿Tu hijo usa gestos simples?
        'Muchas veces al día': 0,
        'Unas cuantas veces al día': 0,
        'Unas cuantas veces por semana': 1,
        'Menos de una vez por semana': 1,
        'Nunca': 1
      },
      10: { // Pregunta 10: ¿Tu hijo se queda mirando fijamente a la nada sin un propósito aparente?
        'Muchas veces al día': 1,
        'Unas cuantas veces al día': 1,
        'Unas cuantas veces por semana': 1,
        'Menos de una vez por semana': 0,
        'Nunca': 0
      }
    };
  
    // Obtener el mapeo específico de la pregunta actual
    const currentMapping = scoreMapping[currentQuestion + 1];
  
    // Retornar el puntaje de la respuesta seleccionada
    return currentMapping[answer] || 0; 
  };
  
  

  const getVisibleQuestions = () => {
    const prevIndex = Math.max(0, currentQuestion - 1);
    const nextEndIndex = Math.min(questions.length, currentQuestion + 4);
    return questions.slice(prevIndex, nextEndIndex);
  };

  return (
    <div className="min-h-screen bg-blue-50 flex">
      <div className="flex-1 flex items-center justify-center p-4">
        <div className="bg-white rounded-xl shadow-lg p-8 w-full max-w-2xl">
          <div className="flex justify-between items-center mb-8">
            <h2 className="text-2xl font-bold text-blue-800">
              Cuéntanos sobre tu hijo
            </h2>
            <span className="text-blue-600 font-semibold">
              Pregunta {currentQuestion + 1} de {questions.length}
            </span>
          </div>

          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="mb-8">
              <h3 className="text-xl text-gray-700 mb-4">
                {questions[currentQuestion].text}
              </h3>

              <div className="space-y-3">
                {questions[currentQuestion].options.map((option) => (
                  <div key={option} className="relative">
                    <label
                      className="flex items-center space-x-3 p-4 rounded-lg border-2 cursor-pointer transition-all duration-200 hover:bg-blue-50 hover:border-blue-400"
                      style={{
                        borderColor: answers[questions[currentQuestion].id] === option ? '#1d4ed8' : '#e5e7eb',
                        backgroundColor: answers[questions[currentQuestion].id] === option ? '#eff6ff' : 'white'
                      }}
                    >
                      <input
                        type="radio"
                        name={`question-${questions[currentQuestion].id}`}
                        value={option}
                        checked={answers[questions[currentQuestion].id] === option}
                        onChange={() => handleOptionSelect(option)}
                        className="h-5 w-5 text-blue-600 border-gray-300 focus:ring-blue-500"
                      />
                      <span className="text-lg text-gray-700">{option}</span>
                    </label>
                  </div>
                ))}
              </div>
            </div>

            <div className="flex justify-between items-center pt-4">
              <button
                type="button"
                onClick={handlePrevious}
                disabled={currentQuestion === 0}
                className="flex items-center px-4 py-2 text-blue-600 font-medium disabled:opacity-50 disabled:cursor-not-allowed hover:bg-blue-50 rounded-lg transition-colors duration-200"
              >
                <ChevronLeft className="w-5 h-5 mr-1" />
                Anterior
              </button>

              {currentQuestion === questions.length - 1 ? (
                <button
                  type="submit"
                  disabled={Object.keys(answers).length !== questions.length}
                  className="px-6 py-2 bg-blue-600 text-white font-semibold rounded-lg shadow-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-colors duration-200 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  Finalizar
                </button>
              ) : (
                <button
                  type="button"
                  onClick={handleNext}
                  disabled={!answers[questions[currentQuestion].id]}
                  className="flex items-center px-4 py-2 text-blue-600 font-medium disabled:opacity-50 disabled:cursor-not-allowed hover:bg-blue-50 rounded-lg transition-colors duration-200"
                >
                  Siguiente
                  <ChevronRight className="w-5 h-5 ml-1" />
                </button>
              )}
            </div>
          </form>
        </div>
      </div>

      <div className="w-56 bg-white shadow-lg p-4 hidden lg:block">
        <h3 className="text-sm font-semibold text-gray-700 mb-3">Preguntas cercanas</h3>
        <div className="space-y-2">
          {getVisibleQuestions().map((question) => {
            const isCurrentQuestion = question.id === questions[currentQuestion].id;
            const isPreviousQuestion = question.id === questions[currentQuestion].id - 1;
            const relativePosition = question.id - questions[currentQuestion].id;

            return (
              <div
                key={question.id}
                className={`p-2 rounded-lg text-sm transition-all ${
                  isCurrentQuestion
                    ? 'bg-blue-100 border border-blue-300'
                    : answers[question.id]
                    ? 'bg-blue-50'
                    : 'bg-gray-50'
                }`}
              >
                <div className="flex items-center gap-2">
                  <span className={`w-5 h-5 flex items-center justify-center rounded-full text-xs ${
                    isCurrentQuestion 
                      ? 'bg-blue-500 text-white' 
                      : isPreviousQuestion
                      ? 'bg-blue-200 text-blue-800'
                      : 'bg-gray-200 text-gray-600'
                  }`}>
                    {question.id}
                  </span>
                  <span className={`text-xs ${
                    isCurrentQuestion 
                      ? 'font-medium text-blue-800' 
                      : relativePosition > 0
                      ? 'text-gray-500'
                      : 'text-gray-400'
                  }`}>
                    {question.text.length > 30 
                      ? question.text.substring(0, 30) + '...' 
                      : question.text}
                  </span>
                </div>
                {answers[question.id] && (
                  <div className="mt-1 text-xs text-blue-600 pl-7">
                    ✓ {answers[question.id]}
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}
